import { useState } from "react";
import { nanoid } from "nanoid";
import NotesList from "./components/NotesList";

var today = new Date();

const App = () => {
  const [notes, setState] = useState([
    {
      id: nanoid(),
      text: "hello",
      time: "12/01/02",
    },
    {
      id: nanoid(),
      text: "bye",
      time: "12/00/01",
    },
    {
      id: nanoid(),
      text: "3rd note",
      time: "12/00/01",
    },
    {
      id: nanoid(),
      text: "4th note",
      time: "12/04/01",
    },
  ]);

  const addNote = (text) => {
    notes.push({
      id: nanoid(),
      text: text,
      time:
        today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds(),
    });
  };

  return (
    <div>
      <nav id="nav">
        <h1>
          Notes <span id="span">By John von Muhlen</span>
        </h1>
      </nav>
      <input
        class="form-control"
        type="text"
        placeholder="Search Notes..."
        aria-label="default input example"
      ></input>
      <NotesList notes={notes} handleAddNote={addNote} />
    </div>
  );
};

export default App;
