import React, { useState } from "react";

const Header = () => {
    var bodyColor =document.querySelector("body").style.backgroundColor
  const changeLight = () => {
    if (bodyColor === "white")
     bodyColor = "black";
  };
  return (
    <div id="nav">
      <h1>
        Notes <span id="span">By John von Muhlen</span>
      </h1>
      <i
        onClick={changeLight}
        className="fa-solid fa-circle-half-stroke"
        id="light"
      ></i>
    </div>
  );
};

export default Header;
