import React, { useState } from "react";

const AddNote = (props) => {
  const [noteText, setNoteText] = useState("");
  const [NotePlaceholder, setNotePlaceholder] = useState("Type to add a note");

  const handleChange = (event) => {
    setNoteText(event.target.value);
  };

  const handleClick = () => {
    if (noteText.trim().length > 0) {
      props.handleAddNote(noteText);
      setNoteText("");
      setNotePlaceholder("Type to add a note");
      document.querySelector(".note.add")[0].style.animation = 'Fade '
    } else {
      setNotePlaceholder("Note can't be empty");
    }
  };

  return (
    <div className="note add">
      <textarea
        placeholder={NotePlaceholder}
        id="input"
        value={noteText}
        onChange={handleChange}
      ></textarea>
      <div className="note-footer">
        <span id="span">200 remaining</span>
        <button type="submit" onClick={handleClick}>
          Save
        </button>
      </div>
    </div>
  );
};

export default AddNote;
