import React, { useState } from "react";

const Header = () => {
  const [classLight, setClass] = useState("fa-solid fa-circle-half-stroke");

  const changeLight = () => {
    if (classLight === "fa-solid fa-circle-half-stroke") {
      setClass("fa-solid fa-circle-half-stroke fa-swap-opacity");
    } else {
      setClass("fa-solid fa-circle-half-stroke");
    }
  };

  return (
    <div id="nav">
      <h1>
        Notes <span id="span">By John von Muhlen</span>
      </h1>
      <i onClick={changeLight} className={classLight} id="light"></i>
    </div>
  );
};

export default Header;
