import { useState } from "react";
import { nanoid } from "nanoid";
import NotesList from "./components/NotesList";
import Search from "./components/Search";

var today = new Date();

const App = () => {
  const [notes, setState] = useState([
    {
      id: nanoid(),
      text: "Sample Note",
      time: today.toLocaleDateString(),
      hidden: false,
    },
  ]);

  const addNote = (text) => {
    const newNote = {
      id: nanoid(),
      text: text,
      time: today.toLocaleDateString(),
      hidden: false,
    };

    const newNotes = [...notes, newNote];
    setState(newNotes);
  };

  const deleteNote = (id) => {
    const filteredNotes = notes.filter((note) => note.id !== id);
    setState(filteredNotes);
  };

  const filterSearch = (search) => {
    const filter = search.toUpperCase();

    for (let i = 0; i < notes.length; i++) {
      let text = notes[i].text;
      if (text.toUpperCase().indexOf(filter) > -1) {
        console.log(notes[i]);
      } else {
        console.log("no notes");
      }
    }
  };

  return (
    <div>
      <nav id="nav">
        <h1>
          Notes <span id="span">By John von Muhlen</span>
        </h1>
      </nav>
      <div id="container">
        <Search search={filterSearch} />
        <NotesList
          notes={notes}
          handleAddNote={addNote}
          deleteNote={deleteNote}
        />
      </div>
    </div>
  );
};

export default App;
